mapper.update:
update product_tbl
	set pdt_buy = #{pdt_buy}
where pdt_num = #{pdt_num}

mapper.select:

select pdt_num, cg_code, cg_code_prt, pdt_name, pdt_price, pdt_discount, pdt_company, 
       pdt_detail, pdt_img, pdt_amount, pdt_buy, pdt_date_sub, pdt_date_up
from (select /*+ index_desc(product_tbl pk_pdt_num) */ 
      rownum rn, pdt_num, cg_code, cg_code_prt, pdt_name, pdt_price, pdt_discount, pdt_company, 
            pdt_detail, pdt_img, pdt_amount, pdt_buy, pdt_date_sub, pdt_date_up
      from product_tbl where pdt_num > 0 and 



	  rownum <= #{pageNum} * #{amount}
	  )
where rn > (#{pageNum} - 1) * #{amount}
