mapper.select:
select count(*)
from member_tbl
where mem_id = #{mem_id}

mapper.update:
update member_tbl
    set mem_sex = #{mem_sex}
    ,   mem_email = #{mem_email}
    ,   mem_zipcode = #{mem_zipcode}
    ,   mem_addr = #{mem_addr}
    ,   mem_addr_d = #{mem_addr_d}
    ,   mem_phone = #{mem_phone}
    ,   mem_accept_e = #{mem_accept_e}
    ,   mem_accept_p = #{mem_accept_p}
    ,   mem_date_up = sysdate
where mem_id = #{mem_id}

update member_tbl 
	set mem_pw = #{mem_pw} 
where mem_id = #{mem_id}

update member_tbl 
	set mem_pw = #{mem_pw} 
where mem_id = #{mem_id}
